let errorLogin=document.getElementById("error-message-login"),errorPassword=document.getElementById("error-message-password"),userUndefined=document.getElementById("user-undefined");function checkLogin(){var e=login.value;if(e){if(e.includes("@"))if(e.substring(0,3).includes("@"))errorLogin.textContent="Зліва від символа @ має бути 3 і більше символів";else{var r=e.indexOf("@"),r=e.substring(r+1);if(r.includes("."))if(r.substring(0,2).includes("."))errorLogin.textContent="У доменному імені має бути мінімум 2 символи";else{var o=r.indexOf(".");if(!(r.substring(o+1).length<2))return e;errorLogin.textContent="У імені регіону має бути мінімум 2 символи"}else errorLogin.textContent="Праворуч від @ має бути доменне ім'я і регіон розділені крапкою"}else errorLogin.textContent="Логін має містити символ @";login.classList.add("form-input-error"),login.value=""}else errorLogin.textContent="Поле не має бути порожнім",login.classList.add("form-input-error")}function chekPassword(){var e=password.value;if(e){if(!(e.length<6))return e;errorPassword.textContent="Пароль має складатися з 6-ти і більше символів",password.classList.add("form-input-error"),password.value=""}else errorPassword.textContent="Поле не має бути порожнім",password.classList.add("form-input-error")}function checkLoginCorrect(){errorLogin.textContent="",userUndefined.textContent="",login.classList.remove("form-input-error")}function chekPasswordCorrect(){errorPassword.textContent="",userUndefined.textContent="",password.classList.remove("form-input-error")}export{errorLogin,errorPassword,userUndefined,checkLogin,chekPassword,checkLoginCorrect,chekPasswordCorrect};